import { useState } from "react";
import "./App.css";
import Axios from 'axios'

function App() {

  const [name, setName] = useState('')
  const [url, setUrl] = useState('')
  const [contact, setContact] = useState('')

  const [profileList, setProfileList] = useState([])

  const addProfile = () => {
    Axios.post('http://localhost:3001/create', {
      name: name, 
      url: url, 
      contact: contact
    })
    .then( () => {
      setProfileList([
        ...profileList,
        {
          name: name, 
          url: url, 
          contact: contact
        }
      ])
    })
  }

const getProfiles = () => {
  Axios.get('http://localhost:3001/profiles').then((response) => {
    setProfileList(response.data)
  })


}

  return (
    <div className="App">
      <div className="form">
        <input type="text" placeholder="Nome" onChange={(e) => {setName(e.target.value)}}/>
        <input type="text" placeholder="URL"  onChange={(e) => {setUrl(e.target.value)}}/>
        <input type="text" placeholder="Contato"  onChange={(e) => {setContact(e.target.value)}}/>
        <button onClick={addProfile}>Adicionar Perfil</button>
        <button onClick={getProfiles}>Get perfis</button>

        {profileList.map((val, key) => {
          return <div className="profile">
                  <p>Nome: {val.name}</p>
                  <p>URL: {val.url}</p>
                  <p>Contato: {val.contact}</p>
                </div>
        })}
      </div>
    </div>
  );
}

export default App;
