import React, { useState, useEffect } from "react";
import Axios from "axios";
import "./Style.css";
import Select from "react-select";

function SearchBar({ placeholder, data }) {

  const [repoList, setRepoList] = useState([]);
  const [profileList, setProfileList] = useState([]);
  const [id, setId] = useState("");

  useEffect(() => {
    Axios.get("http://localhost:3001/profileslist").then((response) => {
      setProfileList(response.data);
      console.log("response.data: " + response.data);
    });
  }, []);

  const profiles = [
    { value: "2", label: "Flávio" },
    { value: "7", label: "Vinícius" },
    { value: "8", label: "Vanessa" },
  ];

  const handleChange = (e) => {
    setId(e.value);
  };

  const getRepo = (id) => {
    Axios.get(`http://localhost:3001/repo/${id}`).then((response) => {
      setRepoList(response.data);
    });
    console.log("fez a request")
  };

  return (
    <div className="search-container">
      <div>
      <Select
        className="search-select"
        classNamePrefix="select"
        isClearable={true}
        isSearchable={true}
        name="profile"
        options={profiles}
        placeholder="Busque um Repo"
        onChange={handleChange}
      />
      <button onClick={() => getRepo(id)}>Nome...</button>
      </div>
      {repoList.map((val, key) => {
        return (
          <div className="repo">
            <p>Título: {val.title}</p>
            <p>por {val.author}</p>
            <p>{val.description}</p>
          </div>
        );
      })}
    </div>
  );
}

export default SearchBar;
