import React, { useState, useEffect } from "react";
import Axios from "axios";
import "./Style.css";
import Select from "react-select";
import BookCard from "../BookCard/BookCard";

function SearchPage({ placeholder, data }) {

  const [repoList, setRepoList] = useState([]);
  const [profileList, setProfileList] = useState([]);
  const [id, setId] = useState("");
  const [name, setName] = useState("");

  useEffect(() => {
    Axios.get("http://localhost:3001/profileslist").then((response) => {
      setProfileList(response.data);
      console.log("response.data: " + response.data);
    });
  }, []);

  const profiles = [
    { value: "2", label: "Flávio" },
    { value: "7", label: "Vinícius" },
    { value: "8", label: "Vanessa" },
  ];

  const handleChange = (e) => {
    setId(e.value);
    setName(e.label);
  };

  const getRepo = (id) => {
    Axios.get(`http://localhost:3001/repo/${id}`).then((response) => {
      setRepoList(response.data);
    });
    console.log("fez a request")
  };

  return (
    <div className="search-container">
      <div className="div-searchbar">
      <Select
        className="search-select"
        classNamePrefix="select"
        isClearable={true}
        isSearchable={true}
        name="profile"
        options={profiles}
        placeholder="Nome..."
        onChange={handleChange}
      />
      <button onClick={() => getRepo(id)}>Buscar</button>
      </div>
      <div>
        <p></p>
      </div>
      {repoList.map((val, key) => {
        return (
          <BookCard title={val.title} author={val.author} description={val.description} url={val.url}/>
        );
      })}
    </div>
  );
}

export default SearchPage;
