import { useState } from "react";
import "./App.css";
import Axios from "axios";
import AddProfileForm from "./FormAddProfiles/AddProfileForm";
import AddBookForm from "./FormAddBook/AddBookForm";

function App() {

  const [newUrl, setNewUrl] = useState("");
  const [profileList, setProfileList] = useState([]);

  const getProfiles = () => {
    Axios.get("http://localhost:3001/profiles").then((response) => {
      setProfileList(response.data);
    });
  };

  const updateProfileUrl = (id) => {
    Axios.put("http://localhost:3001/update", { url: newUrl, id: id }).then(
      (response) => {
        setProfileList(
          profileList.map((val) => {
            return val.id === id
              ? {
                  id: val.id,
                  name: val.name,
                  url: newUrl,
                  contact: val.contact,
                }
              : val;
          })
        );
      }
    );
  };

  const deleteProfile = (id) => {
    Axios.delete(`http://localhost:3001/delete/${id}`).then((response) => {
      setProfileList(
        profileList.filter((val) => {
          return val.id !== id;
        })
      );
    });
  };

  return (
    <div className="App">
            <AddBookForm />
      <AddProfileForm />
        <button onClick={getProfiles}>Get perfis</button>
        {profileList.map((val, key) => {
          return (
            <div className="profile">
              <div>
                <p>Nome: {val.name}</p>
                <p>URL: {val.url}</p>
                <p>Contato: {val.contact}</p>
              </div>
              <div>
                <input
                  type="text"
                  onChange={(event) => {
                    setNewUrl(event.target.value);
                  }}
                />
                <button
                  onClick={() => {
                    updateProfileUrl(val.id);
                  }}
                >
                  {" "}
                  Update
                </button>

                <button
                  onClick={() => {
                    deleteProfile(val.id);
                  }}
                >
                  Delete
                </button>
              </div>
            </div>
          );
        })}
      </div>
  );
}

export default App;
