import { useState } from "react";
import "./Style.css";
import Axios from "axios";
import Select from "react-select";
import AddBookForm from "../FormAddBook/AddBookForm"

function Repo() {


  const [repoList, setRepoList] = useState([]);
  const [id, setId] = useState ('')

  const profiles = [
    { value: "2", label: "Flávio" },
    { value: "7", label: "Vinícius" },
    { value: "8", label: "Vanessa" },
  ];


  var booleanShowComponent = false;
  function toggleShowComponent() {
    booleanShowComponent = !booleanShowComponent
  }

 function ShowAddBookComponent(props) {
  const showComponent = props.showComponent
  if (showComponent) {
    return <AddBookForm />
  }
}

  const handleChange = (e) => {
    setId(e.value);

  }

  const getRepo = (id) => {
    Axios.get(`http://localhost:3001/repo/${id}`).then((response) => {
      setRepoList(response.data);
    });
  };

  return (
    <div className="repo">
      <Select options={profiles} onChange={handleChange} />
      {getRepo(id)}
      {repoList.map((val, key) => {
        return (
          <div className="Repo">
            <p>Título: {val.title}</p>
            <p>por {val.author}</p>
            <p>{val.description}</p>
          </div>
        );
      })}
      <button onClick={toggleShowComponent}>Adicionar Livro ao Repo</button>
      <ShowAddBookComponent showComponent={booleanShowComponent}/>

    </div>
  );
}

export default Repo;
