import Axios from "axios";
import { useState } from "react";

function UpdateProfile() {
  const [newUrl, setNewUrl] = useState("");
  const [newName, setNewName] = useState("");
  const [newContact, setNewContact] = useState("");
  const [profileList, setProfileList] = useState([]);

  const getProfiles = () => {
    Axios.get("http://localhost:3001/profiles").then((response) => {
      setProfileList(response.data);
    });
  };

  const updateProfileUrl = (id) => {
    Axios.put("http://localhost:3001/update", {
      url: newUrl,
      id: id,
      name: newName,
    }).then((response) => {
      setProfileList(
        profileList.map((val) => {
          return val.id === id
            ? {
                id: val.id,
                name: newName,
                url: newUrl,
                contact: newContact,
              }
            : val;
        })
      );
    });
  };

  const deleteProfile = (id) => {
    Axios.delete(`http://localhost:3001/delete/${id}`).then((response) => {
      setProfileList(
        profileList.filter((val) => {
          return val.id !== id;
        })
      );
    });
  };

  return (
    <div className="">
      <button onClick={getProfiles}>Listar perfis</button>
      {profileList.map((val, key) => {
        return (
          <div className="profile">
            <div>
              <p>Nome: {val.name}</p>
              <p>URL: {val.url}</p>
              <p>Contato: {val.contact}</p>
            </div>
            <div>
              <input
                type="text"
                placeholder="novo nome"
                onChange={(event) => {
                  setNewName(event.target.value);
                }}
              />
              <input
                type="text"
                placeholder="nova URL"
                onChange={(event) => {
                  setNewUrl(event.target.value);
                }}
              />
              <input
                type="text"
                placeholder="novo contato"
                onChange={(event) => {
                  setContact(event.target.value);
                }}
              />
              <button
                onClick={() => {
                  updateProfileUrl(val.id);
                }}
              >
                {" "}
                Update
              </button>

              <button
                onClick={() => {
                  deleteProfile(val.id);
                }}
              >
                Delete
              </button>
            </div>
          </div>
        );
      })}
    </div>
  );
}

export default UpdateProfile;
