import Axios from "axios";
import { useState } from "react";
import Select from "react-select";
import NavbarAdmin from "../../layouts/NavbarAdmin/NavbarAdmin";

function UpdateProfile() {
  const [newUrl, setNewUrl] = useState("");
  const [newName, setNewName] = useState("");
  const [newContact, setNewContact] = useState("");
  const [profileList, setProfileList] = useState([]);
  const [id, setId] = useState("");
  const [profile, setProfile] = useState([]);

  const getProfiles = () => {
    Axios.get("http://localhost:3001/profileslist")
      .then((response) => {
        const profiles = response.data;
        setProfileList(profiles);
      })
      .catch((error) => console.error(`Error: ${error}`));
  };

  const getProfile = (id) => {
    Axios.get("http://localhost:3001/profile/:id").then((response) => {
      setProfileList(response.data);
    });
  };
  const handleChange = (e) => {
    setId(e.value);
  };

  const updateProfileUrl = (id) => {
    Axios.put("http://localhost:3001/update", {
      url: newUrl,
      id: id,
      name: newName,
      contact: newContact,
    }).then((response) => {
      setProfileList(
        profileList.map((val) => {
          return val.id === id
            ? {
                id: val.id,
                name: newName,
                url: newUrl,
                contact: newContact,
              }
            : val;
        })
      );
    });
  };

  const deleteProfile = (id) => {
    Axios.delete(`http://localhost:3001/delete/${id}`).then((response) => {
      setProfileList(
        profileList.filter((val) => {
          return val.id !== id;
        })
      );
    });
  };

  return (
    <section>
      <NavbarAdmin />
    <div className="">
    <div className="div-searchbar">
        <Select
          className="search-select"
          classNamePrefix="select"
          isSearchable={true}
          name="profile"
          options={profileList}
          placeholder="Nome..."
          onChange={handleChange}
        />
        <button onClick={() => getProfile(id)}>Buscar</button>
      </div>
      {profileList.map((val, key) => {
        return (
          <div className="container-perfil">
            <div className="div-perfil">
              <p>Nome: {val.name}</p>
              <p>URL: {val.url}</p>
              <p>Contato: {val.contact}</p>
            </div>
            <div className="div-perfil-update">
              <input
                type="text"
                placeholder="novo nome"
                onChange={(event) => {
                  setNewName(event.target.value);
                }}
              />
              <input
                type="text"
                placeholder="nova URL"
                onChange={(event) => {
                  setNewUrl(event.target.value);
                }}
              />
              <input
                type="text"
                placeholder="novo contato"
                onChange={(event) => {
                  setNewContact(event.target.value);
                }}
              />
              <button
                onClick={() => {
                  updateProfileUrl(val.id);
                }}
              >
                {" "}
                Update
              </button>
              <button
                onClick={() => {
                  deleteProfile(val.id);
                }}
              >
                Delete
              </button>
            </div>
          </div>
        );
      })}
    </div>
    </section>

  );
}

export default UpdateProfile;
