import React from "react";
import "./Style.css";


function BookCard({ title, author, url, imgName }) {

  const srcBook = "../../img/"+imgName+".jpg"

  return (  
    <div className="repo">
      <p><a target="_blank" rel="noopener noreferrer" href={url}>{title}</a></p>
      <p>por {author}</p>
      <img className="imgBook" src={srcBook} alt=""/>
    </div>
  );
}

export default BookCard;
