import { useState } from "react";
import "./Style.css";
import Axios from "axios";
import Select from "react-select";
import AddBookForm from "../FormAddBook/AddBookForm";

class Repo extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      repoList: [],
      id: ''
    };
    this.handleInputChange = this.handleInputChange.bind(this);
  }
  // const [repoList, setRepoList] = useState([]);
  // const [id, setId] = useState("");

  handleInputChange(event) {
    const target = event.target;
    const value = target.value;
    const name = target.name;

    this.setState({
      [name]: value
    });
  }

  profiles = [
    { value: "2", label: "Flávio" },
    { value: "7", label: "Vinícius" },
    { value: "8", label: "Vanessa" },
  ];

  // handleChange = (e) => {
  //   this.setId(e.value);
  //   console.log("id setado com:"+id)
  //   getRepo(id)
  // };

  // getRepo(id) {
  //   Axios.get(`http://localhost:3001/repo/${id}`).then((response) => {
  //     setRepoList(response.data);
  //   });
  //   console.log("fez a request")
  // };

  render() {
  return (
    <div className="repo">
      <Select name="id" options={this.profiles} onChange={this.handleInputChange} />
      {this.repoList.map((val, key) => {
        return (
          <div className="Repo">
            <p>Título: {val.title}</p>
            <p>por {val.author}</p>
            <p>{val.description}</p>
          </div>
        );
      })}
      <button >Adicionar Livro ao Repo</button>
      <AddBookForm idProfile={this.id}/>
    </div>
  );    
  }

}

export default Repo;
