import React, {useState} from "react";
import "./Style.css";

function SearchBar({placeholder, data}) {

    const [filterData, setFilterData] = useState([])
    const [wordEntered, setWordEntered] = useState("")

    const handleFilter = (event) => {
        const searchWord = event.target.value
        setWordEntered(searchWord)
        const newFilter = data.filter((value) => {
            return value.label.toLowerCase().includes(searchWord.toLowerCase())
        })
        if (searchWord === "") {
            setFilterData([])
        } else {
            setFilterData(newFilter)
        }
    }

    const setWordOnClick = (word) => {
        setWordEntered(word)
    }

    return(
        <div className="search">
            <div className="searchInput">
                <input type="text" placeholder={placeholder} onChange={handleFilter} value={wordEntered}/>
                <div className="searchIcon"></div>
            </div>
            {filterData.length !== 0 && (
            <div className="dataResult">
                {filterData.map((value, key) => {
                    var label = value.label
                    return <div onClick={setWordOnClick(label)}>{value.label}</div>
                })}
            </div>)}
        </div>
    )
}

export default SearchBar
