import { useState } from "react";
import "./Style.css";
import Axios from "axios";
import Select from "react-select";

function Repo() {
  const [repoList, setRepoList] = useState([]);

  const profiles = [
    { value: 2, label: "Flávio" },
    { value: 7, label: "Vinícius" },
    { value: 8, label: "Vanessa" },
  ];

  const [id, setId] = useState (0)
  const selectedId = () => {
    console.log(id)
  }

  const getRepo = () => {
    Axios.get("http://localhost:3001/repo/:id").then((response) => {
      setRepoList(response.data);
    });
  };

  return (
    <div className="repo">
      <Select onChange={e => setId(e.target.value)} options={profiles}/>
      {repoList.map((val, key) => {
        return (
          <div className="Repo">
            <p>Título: {val.title}</p>
            <p>por {val.author}</p>
            <p>{val.description}</p>
          </div>
        );
      })}
      <button onClick={selectedId}>Buscar Repo</button>
    </div>
  );
}

export default Repo;
