import { useState } from "react";
import "./Style.css";
import Axios from "axios";
import Select from "react-select";
import AddBookForm from "../FormAddBook/AddBookForm";
import BookCard from "../BookCard/BookCard";

function Repo() {
  const [repoList, setRepoList] = useState([]);
  const [id, setId] = useState("");

  const profiles = [
    { value: "2", label: "Flávio" },
    { value: "7", label: "Vinícius" },
    { value: "8", label: "Vanessa" },
  ];

  const handleChange = (e) => {
    setId(e.value);
  };

  const getRepo = (id) => {
    Axios.get(`http://localhost:3001/repo/${id}`).then((response) => {
      setRepoList(response.data);
    });
    console.log("fez a request")
  };

  return (
    <div className="repo-container">
      <Select options={profiles} onChange={handleChange} />
      {repoList.map((val, key) => {
        return (
          <div className="repo">
            <BookCard title={val.title} author={val.author} description={val.description} url={val.url}/>
          </div>

        );
      })}
      <button onClick={() => getRepo(id)}>Buscar Repo</button>
      <AddBookForm idProfile={id}/>
    </div>
  );
}

export default Repo;
