import React, { useState, useEffect } from "react";
import Axios from "axios";
import "./Style.css";
import Select from "react-select";
import BookCard from "../BookCard/BookCard";

function SearchPage() {
  const [repoList, setRepoList] = useState([]);
  const [profileList, setProfileList] = useState([]);
  const [id, setId] = useState("");
  const [name, setName] = useState("");
  const [profilesSelect, setProfilesSelect] = useState([]);

  useEffect(() => {
    Axios.get("http://localhost:3001/profileslist").then((response) => {
      setProfileList(response.data);
    })

    const profiles = profileList.map(p =>({
      "value": p.id,
      "label": p.name
    }))

    this.setProfilesSelect(profiles)
  });


  const handleChange = (e) => {
    setId(e.value);
    setName(e.label);
  };

  const getRepo = (id) => {
    Axios.get(`http://localhost:3001/repo/${id}`).then((response) => {
      setRepoList(response.data);
    });
    console.log("fez a request")
  };

  return (
    <div className="search-container">
      <h1>Leituras recomendadas por quem você admira</h1>
      <div className="div-searchbar">
        <Select
          className="search-select"
          classNamePrefix="select"
          isClearable={true}
          isSearchable={true}
          name="profile"
          options={profiles}
          placeholder="Nome..."
          onChange={handleChange}
        />
        <button onClick={() => getRepo(id)}>Buscar</button>
      </div>
      {repoList.map((val, key) => {
        return (
          <BookCard
            title={val.title}
            author={val.author}
            description={val.description}
            url={val.url}
          />
        );
      })}
      
    </div>
  );
}

export default SearchPage;
