import React, { useState, useEffect } from "react";
import Axios from "axios";
import "./Style.css";
import Select from "react-select";
import BookCard from "../BookCard/BookCard";

function SearchPagetsx() {
  const [repoList, setRepoList] = useState([]);
  const [profileList, setProfileList] = useState([]);
  const [id, setId] = useState("");
  const [name, setName] = useState("");

  const profiles = []

  // carregar perfils para o select
  const getProfiles = () => {
    Axios.get('http://localhost:3001/profileslist')
    .then((response) => {
      const profiles = response.data[0]
      console.log('profiles: '+profiles.id)
      setProfileList(profiles)
      console.log('profileList: '+profileList)
    })
  .catch(error => console.error(`Error: ${error}`))
  }

  const handleChange = (e) => {
    setId(e.value);
    setName(e.label);
  };

  const getRepo = (id) => {
    Axios.get(`http://localhost:3001/repo/${id}`).then((response) => {
      setRepoList(response.data);
    });
    console.log("fez a request")
  };

  useEffect(()=> {
    getProfiles()
  },[])

  return (
    <div className="search-container">
      <h1>Leituras recomendadas por quem você admira</h1>
      <div className="div-searchbar">
        <Select
          className="search-select"
          classNamePrefix="select"
          isClearable={true}
          isSearchable={true}
          name="profile"
          options={profileList}
          placeholder="Nome..."
          onChange={handleChange}
        />
        <button onClick={() => getRepo(id)}>Buscar</button>
      </div>
      {repoList.map((val, key) => {
        return (
          <BookCard
            title={val.title}
            author={val.author}
            description={val.description}
            url={val.url}
          />
        );
      })}
      
    </div>
  );
}

export default SearchPagetsx;
