import { useState, useEffect } from "react";
import "./Style.css";
import Axios from "axios";
import Select from "react-select";
import AddBookForm from "../FormAddBook/AddBookForm";
import BookCard from "../BookCard/BookCard";

function Repo() {
  const [repoList, setRepoList] = useState([]);
  const [id, setId] = useState("");
  const [profileList, setProfileList] = useState([]);

  useEffect(() => {
    getProfiles();
  }, []);

  const getProfiles = () => {
    Axios.get("http://localhost:3001/profileslist")
      .then((response) => {
        const profiles = response.data;
        console.log("profiles: " + profiles);
        setProfileList(profiles);
        console.log("profileList: " + profileList);
      })
      .catch((error) => console.error(`Error: ${error}`));
  };

  const handleChange = (e) => {
    setId(e.value);
  };

  const getRepo = (id) => {
    Axios.get(`http://localhost:3001/repo/${id}`).then((response) => {
      setRepoList(response.data);
    });
  };

  return (
    <div className="repo-container">
      <div className="div-searchbar">
        <Select
          className="search-select"
          classNamePrefix="select"
          isClearable={true}
          isSearchable={true}
          name="profile"
          options={profileList}
          placeholder="Nome..."
          onChange={handleChange}
        />
        <button onClick={() => getRepo(id)}>Buscar</button>
      </div>
      {repoList.map((val, key) => {
        return (
          <div className="repo">
            <BookCard
              title={val.title}
              author={val.author}
              imgName={require(`../../img/${val.img_name}.jpg`)}
              editora={val.editora}
              pages={val.pgs}
              url={val.url}
            />
          </div>
        );
      })}
      <button onClick={() => getRepo(id)}>Buscar Repo</button>
      <AddBookForm idProfile={id} />
    </div>
  );
}

export default Repo;
